sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    //Type appropriate comment here, and begin script below

    var endValue = g_form.getValue('end_date');
    var startValue = g_form.getValue('start_date');

    validateDate(startValue, endValue);

    function validateDate(startDate, endDate) {

        if (!startDate) {
            g_form.addErrorMessage('Please fill in the "When to start your booking?" field first.');
            g_form.clearValue('end_date');
        } else {
            var start = new Date(startDate);
            var end = new Date(endDate);

            if (end <= start) {
                g_form.addErrorMessage('The end date must be later than the start date.');
                g_form.clearValue('end_date');
            }
        }

    }

}
